export default {
  name: "Login",
  data() {
    var validatePassword = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请输入密码'));
      } else {
        if (this.ruleForm.password !== '') {
          this.$refs.ruleForm.validateField('password');
        }
        callback();
      }
    };
    return {
      ruleForm: {
        password: ''
      },
      staffRules: {
        password: [{
          validator: validatePassword,
          trigger: 'blur',
          message: '请输入密码'
        }]
      }
    };
  },
  methods: {}
};
